/**
 * Returns a number rounded to the given decimals.
 * @param value The number.
 * @param decimals The number of decimals.
 */
export declare function roundToDecimals(value: number, decimals: number): number;
/**
 * Returns a number truncated to the given decimals.
 * @param value The number.
 * @param decimals The number of decimals.
 */
export declare function truncToDecimals(value: number, decimals: number): number;
/**
 * Checks if two given float numbers are equal.
 * @param value1 The number.
 * @param value2 The number.
 * @param precision The number.
 */
export declare function floatsAreEqual(value1: number, value2: number, precision?: number): boolean;
