import { Vector3 } from "./vector";
/**
 * Affine, right-handed 3D transformation class.
 * Axes must be perpendicular and right-handed.
 */
export interface AffineTransform3 {
    axisX: Vector3;
    axisY: Vector3;
    axisZ: Vector3;
    origin: Vector3;
    scale: number;
}
/**
 * Returns The rotation transform.
 * @param angleX The Angle X.
 * @param angleY The Angle Y.
 * @param angleZ The Angle Z.
 */
export declare function CreateRotation(angleX: number, angleY: number, angleZ: number): AffineTransform3;
