/**
 * Checks that the URI is valid, roughly according to the same validation rules as the PSet service.
 * @param value The URI to check
 */
export declare function isValidURI(value: string | undefined): boolean;
/**
 * Takes a string that may or may not be a URI, and returns either a valid URI or undefined.
 * If the scheme is not defined, tries to prepend "http://" to make it valid.
 * @param value
 * @param scheme A scheme to add if the value doesn't have one
 */
export declare function normalizeURI(value: string | undefined, scheme?: string): string | undefined;
/**
 * Checks whether the value is a valid URI that can safely be used as a link href.
 * @param value The URI to check
 */
export declare function isClickableLink(value: string | undefined): boolean;
/**
 * Takes an encoded URI (international xn-- URLs, URL-encoded paths) and decodes it for display.
 */
export declare function beautifyLinkText(value: string): string;
